window.setupListeners = function () {
  var dropArea = document.querySelectorAll('.drop-area');
  ;
  ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(function (eventName) {
    dropArea.forEach(function (comp) {
      comp.addEventListener(eventName, preventDefaults, false);
    });
  });
  ;
  ['dragenter', 'dragover'].forEach(function (eventName) {
    dropArea.forEach(function (comp) {
      comp.addEventListener(eventName, highlight, false);
    });
  });
  ;
  ['dragleave', 'drop'].forEach(function (eventName) {
    dropArea.forEach(function (comp) {
      comp.addEventListener(eventName, unhighlight, false);
    });
  });

  function highlight(e) {
    dropArea.forEach(function (comp) {
      comp.classList.add('border', 'border-indigo-600');
    });
  }

  function unhighlight(e) {
    dropArea.forEach(function (comp) {
      comp.classList.remove('border', 'border-indigo-600');
    });
  }

  dropArea.forEach(function (comp) {
    comp.addEventListener('drop', function (e) {
      handleDrop(e, comp.getAttribute('data-id'));
    }, false);
  });

  function preventDefaults(e) {
    e.preventDefault();
    e.stopPropagation();
  }

  function handleDrop(e, unique) {
    var dt = e.dataTransfer;
    var files = dt.files;
    handleFiles(files, unique);
  }
};

